'use strict';

const map = require('broccoli-stew').map;

module.exports = {
  name: 'Ember Transform Addon',

  importTransforms: () => {
    return {
      fastbootShim: tree => {
        return map(tree, content => {
          return `if (typeof FastBoot === 'undefined') { ${content} }`;
        });
      }
    }
  }
};
